mapper.sql:
SELECT

FROM role_permission a WHERE a.del_flag = #{rolePermission.delFlag}

    AND a.id = #{rolePermission.id}


    AND a.type = #{rolePermission.type}


    AND a.permission_id = #{rolePermission.permissionId}


    AND a.role_id = #{rolePermission.roleId}


    AND a.remark = #{rolePermission.remark}


    AND a.create_by = #{rolePermission.createBy}


    AND a.create_time = #{rolePermission.createTime}


    AND a.update_by = #{rolePermission.updateBy}


    AND a.update_time = #{rolePermission.updateTime}
